# Get the directory where the script is located
script_dir = os.path.dirname(os.path.abspath(__file__))

# Construct the full paths for the input and output files
input_file_path = os.path.join(script_dir, 'proverbs_final_400.json')
output_file_path = os.path.join(script_dir, 'proverbs_with_monsters.json')

try:
    with open(input_file_path, 'r', encoding='utf-8') as f:
        proverbs_data = json.load(f)

    for i, proverb in enumerate(proverbs_data):
        if i < len(monster_names):
            proverb['monsterName'] = monster_names[i]

    with open(output_file_path, 'w', encoding='utf-8') as f:
        json.dump(proverbs_data, f, ensure_ascii=False, indent=2)

    print(f"Successfully created {output_file_path}")

except FileNotFoundError:
    print(f"Error: {input_file_path} not found.")
except Exception as e:
    print(f"An error occurred: {e}")